mapper.select:
/* kt.aivle.rslt_list.RsltListMapper.getActiveGongoSn*/
SELECT COALESCE(
  (SELECT g.gongo_sn
  FROM gongo AS g
  WHERE g.use_yn = 'Y'),
  0
) AS result;

/* kt.aivle.rslt_list.RsltListMapper.getDtlSnByGongoSn*/
SELECT
gd.gongo_dtl_sn
FROM gongo_dtl AS gd
WHERE gd.gongo_sn = #{sn}

/* kt.aivle.rslt_list.RsltListMapper.getJutaekSnByDtlSn*/
SELECT
  gd.jutaek_sn
FROM gongo_dtl AS gd
WHERE gd.gongo_dtl_sn IN(

  #{item}

);

/* kt.aivle.rslt_list.RsltListMapper.getJutaekListCnt*/
SELECT
COUNT(*)
FROM jutaek_dtl AS jd
LEFT JOIN jutaek AS j ON j.jutaek_sn = jd.jutaek_sn
WHERE jd.jutaek_sn IN(

  #{item}

)

/* kt.aivle.rslt_list.RsltListMapper.getJutaekList*/ SELECT jd.jutaek_dtl_sn , jd.jutaek_sn , jd.jutaek_size , jd.residential_area , jd.common_area , jd.other_area , jd.jutaek_type , jd.guarantee , jd.monthly FROM jutaek_dtl AS jd LEFT JOIN jutaek AS j ON j.jutaek_sn = jd.jutaek_sn WHERE jd.jutaek_sn IN(
#{item}
)
LIMIT #{pageOffset}, #{pageSize}
